package entropy

import (
	"testing"
)

func TestGetShannons(t *testing.T) {
	var tests = []struct {
		input string
		score int
	}{
		{
			"",
			0,
		},
		{
			"1111111111111111111111",
			0,
		},
		{
			"1223334444",
			185,
		},
		{
			"tr,BfXBRTjPBn7xUF@csyCpCNpeGpqhzHbs9FYmGUGsmiCCEPTPgue>Bie2ftpXk",
			588,
		},
		{
			"AoXWDwkkddxEhttQtJRhP>$UXHfMZ?7GMzrjkW8DUJZV4qoHMCinozsEcsgaWjgy",
			523,
		},
		{
			"",
			600,
		},
		{
			"",
			600,
		},
	}
	for i, test := range tests {
		if GetShannons(test.input) > test.score {
			t.Fatalf("Test %d failed with score %d (expected %d)", i, test.score, GetShannons(test.input))
		}
	}
}
